import { Component, ElementRef, ViewChild, AfterViewInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';

@Component({
  selector: 'app-video-page',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="video-page-container">
      <video 
        #introVideo 
        class="fullscreen-video" 
        autoplay 
        muted 
        playsinline
        (ended)="onVideoEnd()"
        (loadeddata)="onVideoLoaded()"
        (canplay)="onVideoCanPlay()">
        <source [src]="videoUrl" type="video/mp4">
      </video>
    </div>
  `,
  styles: [`
    .video-page-container {
      position: fixed;
      top: 0;
      left: 0;
      width: 100vw;
      height: 100vh;
      background: black;
      display: flex;
      align-items: center;
      justify-content: center;
      overflow: hidden;
    }
    
    .fullscreen-video {
      max-width: 100vw;
      max-height: 100vh;
      width: 100%;
      height: 100%;
      object-fit: contain;
    }
  `]
})
export class VideoPageComponent implements AfterViewInit {
  @ViewChild('introVideo') introVideo!: ElementRef<HTMLVideoElement>;
  
  readonly videoUrl = 'https://website-juniorliu.s3.us-east-2.amazonaws.com/is-that-you.mp4';
  
  constructor(private router: Router) {}
  
  ngAfterViewInit() {
    // Try to play video after view is initialized
    setTimeout(() => {
      if (this.introVideo && this.introVideo.nativeElement) {
        this.introVideo.nativeElement.play().catch(error => {
          console.error('Error playing video:', error);
        });
      }
    }, 100);
  }
  
  onVideoLoaded() {
    console.log('Video loaded and ready to play');
    // Ensure video plays when loaded
    if (this.introVideo && this.introVideo.nativeElement) {
      this.introVideo.nativeElement.play().catch(error => {
        console.error('Error playing video after load:', error);
      });
    }
  }
  
  onVideoCanPlay() {
    console.log('Video can play');
    // Force play when video is ready
    if (this.introVideo && this.introVideo.nativeElement) {
      this.introVideo.nativeElement.play().catch(error => {
        console.error('Error playing video when ready:', error);
      });
    }
  }
  
  onVideoEnd() {
    console.log('Video ended, navigating to index');
    // Navigate to index page when video ends
    this.router.navigate(['/index']);
  }
}
