import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-resume-page',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <div class="resume-container">
      <!-- Download Button -->
      <div class="download-section">
        <a [href]="resumePdfUrl" download="resume-25.pdf" class="download-button">
          Download PDF
        </a>
        <a routerLink="/" class="back-button">← Back to Home</a>
      </div>
      
      <!-- Resume Display -->
      <div class="resume-display">
        <iframe 
          [src]="resumePdfUrl" 
          class="pdf-viewer"
          type="application/pdf">
          <p>Your browser does not support PDFs. 
            <a [href]="resumePdfUrl" download="resume-25.pdf">Download the PDF</a>
          </p>
        </iframe>
        
        <!-- Fallback for browsers that don't support iframe PDF -->
        <div class="resume-fallback" *ngIf="showFallback">
          <div class="fallback-content">
            <h2>Resume Preview</h2>
            <p>Click the "Download PDF" button above to view your resume.</p>
            <a [href]="resumePdfUrl" download="resume-25.pdf" class="download-button-fallback">
              Download Resume PDF
            </a>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .resume-container {
      min-height: 100vh;
      background: black;
      display: flex;
      flex-direction: column;
      position: relative;
    }
    
    .download-section {
      position: fixed;
      top: 0;
      left: 0;
      right: 0;
      z-index: 100;
      background: rgba(0, 0, 0, 0.9);
      padding: 1rem;
      display: flex;
      justify-content: center;
      align-items: center;
      gap: 2rem;
      backdrop-filter: blur(10px);
      border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    }
    
    .download-button {
      display: inline-block;
      padding: 1rem 2rem;
      background: rgba(255, 255, 255, 0.2);
      color: white;
      text-decoration: none;
      border-radius: 8px;
      border: 2px solid rgba(255, 255, 255, 0.3);
      transition: all 0.3s ease;
      font-weight: bold;
      font-size: 1.1rem;
    }
    
    .download-button:hover {
      background: rgba(255, 255, 255, 0.3);
      transform: translateY(-2px);
      box-shadow: 0 5px 15px rgba(255, 255, 255, 0.2);
    }
    
    .back-button {
      display: inline-block;
      padding: 0.8rem 1.5rem;
      background: rgba(255, 255, 255, 0.1);
      color: white;
      text-decoration: none;
      border-radius: 6px;
      border: 1px solid rgba(255, 255, 255, 0.2);
      transition: all 0.3s ease;
      font-size: 0.9rem;
    }
    
    .back-button:hover {
      background: rgba(255, 255, 255, 0.2);
      transform: translateY(-1px);
    }
    
    .resume-display {
      flex: 1;
      padding-top: 100px; /* Account for fixed header */
    }
    
    .pdf-viewer {
      width: 100%;
      height: calc(100vh - 100px);
      border: none;
      background: white;
    }
    
    .resume-fallback {
      display: none;
      padding: 2rem;
      text-align: center;
      color: white;
    }
    
    .fallback-content h2 {
      font-size: 2rem;
      margin-bottom: 1rem;
      color: white;
    }
    
    .fallback-content p {
      font-size: 1.2rem;
      margin-bottom: 2rem;
      color: #ccc;
    }
    
    .download-button-fallback {
      display: inline-block;
      padding: 1.5rem 3rem;
      background: rgba(255, 255, 255, 0.2);
      color: white;
      text-decoration: none;
      border-radius: 8px;
      border: 2px solid rgba(255, 255, 255, 0.3);
      transition: all 0.3s ease;
      font-weight: bold;
      font-size: 1.2rem;
    }
    
    .download-button-fallback:hover {
      background: rgba(255, 255, 255, 0.3);
      transform: translateY(-2px);
    }
    
    @media (max-width: 768px) {
      .download-section {
        flex-direction: column;
        gap: 1rem;
        padding: 0.8rem;
      }
      
      .download-button {
        padding: 0.8rem 1.5rem;
        font-size: 1rem;
      }
      
      .back-button {
        padding: 0.6rem 1.2rem;
        font-size: 0.8rem;
      }
      
      .resume-display {
        padding-top: 120px;
      }
      
      .pdf-viewer {
        height: calc(100vh - 120px);
      }
    }
    
    @media (max-width: 480px) {
      .download-section {
        padding: 0.5rem;
      }
      
      .download-button {
        padding: 0.7rem 1.2rem;
        font-size: 0.9rem;
      }
      
      .resume-display {
        padding-top: 140px;
      }
      
      .pdf-viewer {
        height: calc(100vh - 140px);
      }
    }
  `]
})
export class ResumePageComponent {
  readonly resumePdfUrl = 'https://website-juniorliu.s3.us-east-2.amazonaws.com/resume-25.pdf';
  showFallback = false;
  
  ngOnInit() {
    // Check if PDF can be displayed
    setTimeout(() => {
      this.checkPdfSupport();
    }, 1000);
  }
  
  private checkPdfSupport() {
    // Basic check for PDF support
    const isChrome = /Chrome/.test(navigator.userAgent) && /Google Inc/.test(navigator.vendor);
    const isFirefox = /Firefox/.test(navigator.userAgent);
    const isSafari = /Safari/.test(navigator.userAgent) && /Apple Computer/.test(navigator.vendor);
    
    // Most modern browsers support PDF in iframe, but we can add fallback if needed
    if (!isChrome && !isFirefox) {
      // Safari and some other browsers might need fallback
      this.showFallback = true;
    }
  }
}
