import { Component, OnInit, ElementRef, ViewChild, AfterViewInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <div class="home-container">
      <!-- Video Section -->
      <div class="video-container" [class.fade-out]="videoEnded" *ngIf="!videoEnded">
        <video 
          #introVideo 
          class="intro-video" 
          autoplay 
          muted 
          playsinline
          (ended)="onVideoEnd()"
          (loadeddata)="onVideoLoaded()"
          (canplay)="onVideoCanPlay()">
          <source [src]="videoUrl" type="video/mp4">
        </video>
      </div>
      
      <!-- Profile Picture Section -->
      <div class="profile-section" [class.show]="videoEnded && showProfile">
        <div class="profile-container" (click)="nextProfilePicture()">
          <img 
            [src]="currentProfilePicture" 
            [alt]="'Profile Picture ' + (currentProfileIndex + 1)"
            class="profile-image">
        </div>
      </div>
      
      <!-- Navigation Icons -->
      <div class="navigation-icons" [class.show]="videoEnded && showProfile">
        <a href="https://www.linkedin.com/in/junior-liu-548241102/" target="_blank" class="nav-icon" title="LinkedIn">
          <img [src]="linkedinIconUrl" alt="LinkedIn" class="icon">
        </a>
        
        <a [href]="cvUrl" target="_blank" class="nav-icon" title="CV">
          <img [src]="cvIconUrl" alt="CV" class="icon">
        </a>
        
        <a href="http://www.seattlemj.com" target="_blank" class="nav-icon" title="MJClub">
          <img [src]="mjClubIconUrl" alt="MJClub" class="icon">
        </a>
        
        <a routerLink="/car" class="nav-icon" title="Car Page">
          <img [src]="porscheIconUrl" alt="Car Page" class="icon">
        </a>
        
        <a routerLink="/tennis" class="nav-icon" title="Tennis Page">
          <img [src]="tennisIconUrl" alt="Tennis Page" class="icon">
        </a>
      </div>
    </div>
  `,
  styles: [`
    .home-container {
      min-height: 100vh;
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      background: black;
      position: relative;
      overflow: hidden;
    }
    
    .video-container {
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      display: flex;
      align-items: center;
      justify-content: center;
      transition: opacity 2s ease-in-out;
    }
    
    .video-container.fade-out {
      opacity: 0;
      pointer-events: none;
    }
    
    .intro-video {
      max-width: 100%;
      max-height: 100%;
      object-fit: contain;
    }
    
    .profile-section {
      opacity: 0;
      transform: scale(0.8);
      transition: all 2s ease-in-out;
    }
    
    .profile-section.show {
      opacity: 1;
      transform: scale(1);
    }
    
    .profile-container {
      cursor: pointer;
      transition: transform 0.3s ease;
      margin-bottom: 3rem;
    }
    
    .profile-container:hover {
      transform: scale(1.05);
    }
    
    .profile-image {
      max-width: 400px;
      max-height: 400px;
      width: auto;
      height: auto;
      object-fit: contain;
      border: 5px solid rgba(255, 255, 255, 0.3);
      box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
    }
    
    .navigation-icons {
      display: flex;
      gap: 2rem;
      opacity: 0;
      transform: translateY(30px);
      transition: all 1.5s ease-in-out;
    }
    
    .navigation-icons.show {
      opacity: 1;
      transform: translateY(0);
    }
    
    .nav-icon {
      display: flex;
      align-items: center;
      justify-content: center;
      width: 60px;
      height: 60px;
      background: rgba(255, 255, 255, 0.2);
      border-radius: 50%;
      text-decoration: none;
      transition: all 0.3s ease;
      backdrop-filter: blur(10px);
      border: 2px solid rgba(255, 255, 255, 0.3);
    }
    
    .nav-icon:hover {
      background: rgba(255, 255, 255, 0.3);
      transform: translateY(-5px);
      box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
    }
    
    .nav-icon .icon {
      width: 30px;
      height: 30px;
      object-fit: contain;
    }
    
    @media (max-width: 768px) {
      .profile-image {
        max-width: 300px;
        max-height: 300px;
      }
      
      .navigation-icons {
        gap: 1.5rem;
      }
      
      .nav-icon {
        width: 50px;
        height: 50px;
      }
      
      .nav-icon .icon {
        width: 25px;
        height: 25px;
      }
    }
  `]
})
export class HomeComponent implements OnInit, AfterViewInit {
  @ViewChild('introVideo') introVideo!: ElementRef<HTMLVideoElement>;
  
  // S3 URLs for assets
  readonly videoUrl = 'https://website-juniorliu.s3.us-east-2.amazonaws.com/is-that-you.mp4';
  readonly profilePictures = [
    'https://website-juniorliu.s3.us-east-2.amazonaws.com/jr1.jpg',
    'https://website-juniorliu.s3.us-east-2.amazonaws.com/jr2.jpg',
    'https://website-juniorliu.s3.us-east-2.amazonaws.com/jr3.jpg',
    'https://website-juniorliu.s3.us-east-2.amazonaws.com/jr4.jpg'
  ];
  readonly linkedinIconUrl = 'https://website-juniorliu.s3.us-east-2.amazonaws.com/linkedin.png';
  readonly cvUrl = 'https://website-juniorliu.s3.us-east-2.amazonaws.com/resume-25.pdf';
  readonly cvIconUrl = 'https://website-juniorliu.s3.us-east-2.amazonaws.com/cv.png';
  readonly mjClubIconUrl = 'https://website-juniorliu.s3.us-east-2.amazonaws.com/mj.png';
  readonly porscheIconUrl = 'https://website-juniorliu.s3.us-east-2.amazonaws.com/porsche.jpg';
  readonly tennisIconUrl = 'https://website-juniorliu.s3.us-east-2.amazonaws.com/RF.jpg';
  
  currentProfileIndex = 0;
  videoEnded = false;
  showProfile = false;
  
  get currentProfilePicture(): string {
    return this.profilePictures[this.currentProfileIndex];
  }
  
  ngOnInit() {
    // Component initialization
  }
  
  ngAfterViewInit() {
    // Try to play video after view is initialized
    setTimeout(() => {
      if (this.introVideo && this.introVideo.nativeElement) {
        this.introVideo.nativeElement.play().catch(error => {
          console.error('Error playing video:', error);
        });
      }
    }, 100);
  }
  
  onVideoLoaded() {
    console.log('Video loaded and ready to play');
    // Ensure video plays when loaded
    if (this.introVideo && this.introVideo.nativeElement) {
      this.introVideo.nativeElement.play().catch(error => {
        console.error('Error playing video after load:', error);
      });
    }
  }
  
  onVideoCanPlay() {
    console.log('Video can play');
    // Force play when video is ready
    if (this.introVideo && this.introVideo.nativeElement) {
      this.introVideo.nativeElement.play().catch(error => {
        console.error('Error playing video when ready:', error);
      });
    }
  }
  
  onVideoEnd() {
    console.log('Video ended, starting fade transition');
    this.videoEnded = true;
    
    // Show profile after video fade completes
    setTimeout(() => {
      this.showProfile = true;
    }, 500);
  }
  
  nextProfilePicture() {
    this.currentProfileIndex = (this.currentProfileIndex + 1) % this.profilePictures.length;
    console.log(`Switched to profile picture ${this.currentProfileIndex + 1}`);
  }
}
